import React , { useState } from "react";

const AuthContext = React.createContext({
    isLoggedIn : false,
    onLogOut : () => {},
    onLogin : () => {}
});

export const AuthContextProvider =(props) => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const logoutHandler = () => {
        setIsLoggedIn(false);
    };

    const loginHandler = () => {
        setIsLoggedIn(true)
    }
}

export default AuthContext;
